import React, {useEffect, useState} from 'react';
import TinderCard from "react-tinder-card";
import database from './firebase.js';
import "./TinderCard.css";

function TinderCards() {
    const [people,setPeople]=useState([{
        name:'Aya',
        url: 'http://stephenesherman.com/wp-content/uploads/2018/05/sexy-japanese-model-04.jpg'
    },
    {
        name: 'Yumi',
        url: 'https://healthtostyle.com/wp-content/uploads/2020/06/Masami-Nagasawa-564x445.jpg'
    },
    {
        name: 'Sayuri',
        url: 'https://qph.fs.quoracdn.net/main-qimg-d8da28c9dc0425651104ae58e1322e5f'
    },
    {
        name: 'Miyuki',
        url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTiGCZx8D3mCqWFgjYAondp8Fiponwm60rAQw&usqp=CAU'
    }]);


    useEffect(() => {
        database.collection('people').onSnapshot(snapshot => (
            setPeople(snapshot.docs.map(doc => doc.data()))
            ))
    },[people]);

    return(
        <div>
            <div className="tinderCards_container">
            {people.map(person => (
                <TinderCard 
                className="swipe" 
                key={person.name}
                preventSwipe={["up","down"]}>
                    <div
                    style={{ backgroundImage: `url(${person.url})` }} 
                    className="card">
                        
                        <h3>{person.name} </h3>
                    </div>
                </TinderCard>
            ))}
            </div>
        </div>
    );
}

export default TinderCards;
